package loops;

public class NestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int factor=0;
int counter=0;
for(factor=1; factor<=4; factor++){
for (counter=1; counter<=5;counter++)
	System.out.println((factor*counter)+" ");
			}
System.out.println();
		}
}